import './assets/scss/App.scss';
import 'bootstrap/dist/css/bootstrap.min.css';
import Footer from './layouts/Footer'
import Head from './layouts/Head'
import Home from './Pages/Home'
import Contact from './Pages/Contact'
import { Routes, Route, } from "react-router-dom";
import OurMission from './Pages/OurMission';
import Adopt from './Pages/Adopt';
import Signup from './Pages/Signup';
import { useState, useEffect } from 'react';

function App() {

//when user scroll down , console log "scrolling" , when reaching the bottom of the page, console log "bottom of page"
const [scrolling, setScrolling] = useState(false);
const [bottomOfPage, setBottomOfPage] = useState(false);

useEffect(() => {
  const handleScroll = () => {
    setScrolling(true);
    setTimeout(() => {
      setScrolling(false);
    }, 500);
    if (
      window.innerHeight + document.documentElement.scrollTop
      === document.documentElement.offsetHeight
    ) {
      setBottomOfPage(true);
    }
  };
  window.addEventListener('scroll', handleScroll);
  return () => window.removeEventListener('scroll', handleScroll);
}, []);

console.log(scrolling);
console.log(bottomOfPage);



  return (
  <>
   <Head/> 
   <Routes>
      <Route path=""   >
        <Route path="" element={ <Home/>} />
        <Route path="contact" element={ <Contact/>} />
        <Route path="ourmission" element={ <OurMission/>} />
        <Route path="adopt" element={ <Adopt/>} />
        <Route path="singup" element={ <Signup/>} />
      </Route>

      </Routes>
  <Footer/>

  </>
  );
}

export default App;
