        return 
    } catch (error) {
      // console.log(error);
    }
  }

setInterval(() => {
    console.log("stpe1 getFromLocalForge");
    getFromLocalForge(token)

}, [50000])






  return (
    <AuthContext.Provider value={{userData  }}>
    {children}
    </AuthContext.Provider>
  );
}

