        const available = dataFromChild[0].find((item) => item === "available");
        const foster = dataFromChild[0].find((item) => item === "foster");
        //get in type
        const dog = dataFromChild[0].find((item) => item === "dog");
        const cat = dataFromChild[0].find((item) => item === "cat");
        const other = dataFromChild[0].find((item) => item === "other");
        //get in height


        //get in weight

        //get in age


        //set in adopt status

        if (adopt) {
          acc.adoption_status = { ...acc.adoption_status, adopt: adopt };
        }
        if (available) {
          acc.adoption_status = { ...acc.adoption_status, available: available };
        }
        if (foster) {
          acc.adoption_status = { ...acc.adoption_status, foster: foster };
        }
        //set in type
        if (dog) {
          acc.type = { ...acc.type, dog: dog };
        }
        if (cat) {
          acc.type = { ...acc.type, cat: cat };
        }
        if (other) {
          acc.type = { ...acc.type, other: other };
        }
        //set in height

        //set in weight



    }, { adoption_status: {} });



    setFilter(obj)
    console.log(filter, "filter");

      // setFilter([{type: data.type },{adoption_status: data.adoption_status}])
    getPetData(filter)
  }, [])



const cardsList = petArr.map((pet) => {

      return <PetCardSearch pet={pet} />
    });

    //getting data from child
  const [dataFromChild, setDataFromChild] = useState([]);
  const handleDataFromChild = (data) => {
    setDataFromChild(data);
    console.log(data, "data");
  };


  return (

    <div className="adoptContainer">
      <div className="adoptHeader">
        <h1>Adopt a Pet</h1>
      </div>

      <div className="sideNav">
        <AdoptForm 

        onData={handleDataFromChild}

        />
      </div>
      <div className="petsContainer">


      <Line addClass="between">
      {cardsList} 
      </Line>
      </div>
    </div>
  );
}
