import { useState } from "react";
import { createContext } from "react";
import localForage from "localforage";
import UserApi from "./Data/UserApi";

const AuthContext = createContext();
const userApi = new UserApi();
function AuthContextProvider({ children }) {
  const [token, setToken] = useState("");
  const [userData, setUserData] = useState();
  const [isLogged, setIsLogged] = useState(false);
  const [userDataStored, setUserDataStored] = useState(false);

  async function getUserData() {
    //take id from forage
    const petApp = await localForage.getItem("petApp");
    const userData = await userApi.getUserById(petApp._id);
    setUserData(userData);
    setUserDataStored(true);
    console.log("userDataStored", userDataStored);
  }
  if (isLogged && !userDataStored) {
    getUserData();
  }

  if (!isLogged) {
    setTimeout(() => {
      setUserDataStored(false);
      userApi.validateUser().then((res) => {
        if (res.status === 200) {
          setIsLogged(true);
        }
      });
    }, 10000);
  }

  async function checkToken() {
    try {
      const tokenFromStorage = await userApi.getToken();
      userApi.validateUser().then((res) => {
        if (res.status === 200) {
          setIsLogged(true);
        }
      });
    } catch (error) {
      console.log(error);
    }
  }
  checkToken();

  return (
    <AuthContext.Provider
      value={{ userDataStored, userData, setUserData, isLogged, setIsLogged }}
    >
      {children}
    </AuthContext.Provider>
  );
}

export { AuthContext, AuthContextProvider };
