import React from "react";
import { useState, useEffect } from "react";
import { Grid } from "../assets/UiKit/grid/Gird";
import { Line, Rows } from "../assets/UiKit/Line/Line";
import { useNavigate } from "react-router-dom";
import "../assets/scss/Login.scss";
import UserAPI from ".././Data/UserApi";
import { AuthContext } from ".././AuthContextProvider";
import { useContext } from "react";
import localforage from "localforage";
import Loading from "../assets/UiKit/premade_componenets/loading/Loading";

export default function Login() {
  const navigate = useNavigate();
  const [registerEmail, setEmail] = useState("");
  const [registerPassWord, setPassword] = useState("");
  const [errorMessage, setErrorMessage] = useState("");
  const {userData, setUserData } = useContext(AuthContext);
  const userApi = new UserAPI();

  const [userId, setUserId] = useState("");
  const [isLogged, setIsLogged] = useState(false);
  const [loading, setLoading] = useState(false);

  //login get confirmation
  async function handleSubmit(e) {
    e.preventDefault();
    try {
      setLoading(true)
      const userLogIn = await userApi.login(registerEmail, registerPassWord);
      console.log(userLogIn);
      if (userLogIn.status === 200) {
        console.log();
        // setIsLogged(true);
        // setingToken( userLogIn.token,userLogIn.user._id);
      }
      // setLoading(false)
    } catch (error) {
      console.log(error);
    }
  }
  // async function setingToken(token, id) {
  //   await localforage.setItem(petApp:token, token);


  // }
  // isLogged ? userApi.setToken(userToken, userId) : console.log("not logged");

  if (isLogged === true) {
    const userDataFromApi = async () => await userApi.getUserData(userId);
    setUserData(userDataFromApi);
    navigate("/");
  }

  return (
    <div className="loginContainer">
      <h2>LogIn</h2>
      <Grid>
        <Rows>
          <form>
            <Line>
              <label>Email</label>
              <input
                type="email"
                value={registerEmail}
                onChange={(e) => setEmail(e.target.value)}
              />
            </Line>

            <Line>
              <label>Password</label>
              <input
                type="password"
                value={registerPassWord}
                onChange={(e) => setPassword(e.target.value)}
              />
            </Line>
            <Line>

            </Line>
            <Line>
            <div>
            <button className="nakedButton" onClick={handleSubmit}>
                Submit
              </button>
            </div>


              <h5>{errorMessage}</h5>
            </Line>
            <Line>
              <div> {loading ? <Loading /> : null}</div>
            </Line>
          </form>
        </Rows>
      </Grid>
    </div>
  );
}
