import React from "react";
import localforage from "localforage";
import { useNavigate } from "react-router-dom";

class UserApi extends React.Component {
  constructor(props) {
    super(props);
    this.url = "http://localhost:8000";
    this.state = {
      users: [],
      f_name: "",
      l_name: "",
      email: "",
      password: "",
      password_validate: "",
      phone_num: "",
      bio: "",
      loading: true,
      error: false,
    };
  }

  async setToken(token, user) {
    try {
      const petApp = await localforage.getItem("petApp");

      if (petApp !== null) {
        await localforage.setItem("petApp", {
          token: token,
          _id: user._id,
        });
      } else {
        await localforage.setItem("petApp", {
          token: token,
          _id: user._id,
        });
      }
    } catch (error) {
      console.log(error);
    }
  }

  async getToken() {
    try {
      const petApp = await localforage.getItem("petApp");
      return petApp;
    } catch (error) {
      console.log(error);
    }
  }

  async newUser(
    f_name,
    l_name,
    email,
    password,
    password_validate,
    phone_num,
    bio
  ) {
    try {
      const response = await fetch("http://localhost:8000/signup", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          f_name: f_name,
          l_name: l_name,
          email: email,
          password: password,
          password_validate: password_validate,
          phone_num: phone_num,
          bio: bio,
        }),
      });
      const data = await response.json();
      console.log(data);
      localStorage.setItem("petApp", data.token);
      //redirect to home page

      return;
    } catch (error) {
      console.log(error);
    }
  }

  async login(email, password) {
    const url = "http://localhost:8000/login/";

    const body = JSON.stringify({ email, password });
    const headers = {
      "Content-Type": "application/json",
      Accept: "application/json",
    };

    try {
      const response = await fetch(url, { method: "POST", headers, body });
      const data = await response.json();
      console.log(data);
      if (response.ok) {
        return { user: data.user, token: data.token, status: response.status };
      } else {
        throw new Error(data.message || "Login failed");
      }
    } catch (error) {
      console.error(error);
      throw new Error("Network error");
    }
  }

  async logout() {
    try {
      await localforage.removeItem("petApp");
    } catch (error) {
      console.log(error);
    }
  }
  async getUserById(_id, token) {
    const bearer = "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJlbWFpbCI6ImFAYS5jb20iLCJwYXNzd29yZCI6IjAzYWM2NzQyMTZmM2UxNWM3NjFlZTFhNWUyNTVmMDY3OTUzNjIzYzhiMzg4YjQ0NTllMTNmOTc4ZDdjODQ2ZjQiLCJpYXQiOjE2NzgxMjU2NjV9.Gfv626CV-_ocTVyZSX_CTB9kvOpG7rAP_5nZH_FF-Pw"
    console.log(bearer);

    const url = `http://localhost:8000/user/${_id}`;
    try {
      const response = await fetch(url, {
        method: "GET",
        headers: {
          Authorization: `Bearer ${bearer}`,
        },
      });
      const result = await response.json();
      return {
        status: response.status,
        data: result,
      };
    } catch (error) {
      console.error(error);
    }
  }

  render() {
    if (this.state.loading) {
      return <div>loading...</div>;
    }
    if (this.state.error) {
      return <div>error...</div>;
    }
    return (
      <div>
        <h1>Users</h1>
        <ul>
          {this.state.users.map((user) => (
            <li key={user.id}>
              {user.f_name} {user.l_name}
            </li>
          ))}
        </ul>
      </div>
    );
  }
  //make function that validate user id and token from forage with the api 
  async validateUser() {
    const token = await this.getToken();
    const user = await this.getUserById(token._id, token.token);
    if (user.status === 200) {
      return true;
    } else {
      return false;
    }
  }
}

export default UserApi;
