import React from 'react'
import { useState,useEffect } from 'react'
import { Grid } from "../assets/UiKit/grid/Gird";
import { Line,Rows } from "../assets/UiKit/Line/Line";
import { useNavigate } from "react-router-dom";
import '../assets/scss/Login.scss'
import UserAPI from '.././Data/UserApi'
import { AuthContext } from '.././AuthContextProvider'
import { useContext } from 'react'
import localforage from 'localforage'

export default function Login() {
    const navigate = useNavigate();
    const [registerEmail, setEmail] = useState("");
    const [registerPassWord, setPassword] = useState("");
    const [errorMessage, setErrorMessage] = useState("");
    const { userData, setUserData } = useContext(AuthContext);
    const userApi = new UserAPI();
    const [token, setToken] = useState("");
    const [_id, setAuthId] = useState("");

    useEffect(() => {
        const getFromLocalForge = async () => {
            try {
                const petApp = await localforage.getItem("petApp");
                if (petApp === null) {
                    return;
                }
                if (petApp._id == null) {
                    return;
                }
                userApi.setToken(petApp.token, petApp._id);
                setToken(petApp.token);
            } catch (error) { 
                console.log(error);
            }
        }
        getFromLocalForge();
    }, [token, _id])



      async function handleSubmit(e) {
        e.preventDefault();
        try {
        await  userApi.login(registerEmail, registerPassWord)

        } catch (error) {
          console.log(error)
        }




