import './assets/scss/App.scss';
import 'bootstrap/dist/css/bootstrap.min.css';
import Footer from './layouts/Footer'
import Head from './layouts/Head'
import Home from './Pages/Home'
import Contact from './Pages/Contact'
import { Routes, Route, } from "react-router-dom";
import OurMission from './Pages/OurMission';
import Adopt from './Pages/Adopt';
import Signup from './Pages/Signup';
import { useState, useEffect } from 'react';

function App() {


const [bottomOfPage, setBottomOfPage] = useState(false);

//if page is scroll all the way down, show footer
useEffect(() => {
  const updateDisplay = () => {
    if (window.innerHeight + document.documentElement.scrollTop === document.documentElement.offsetHeight) {
      setBottomOfPage(true);
      console.log('bottom of page');
    } else {
      setBottomOfPage(false);
    }
  };
  updateDisplay();
  window.addEventListener('scroll', updateDisplay);
  return () => {
    window.removeEventListener('scroll', updateDisplay);
  };
}, []);





  return (
  <>
   <Head/> 
   <Routes>
      <Route path=""   >
        <Route path="" element={ <Home/>} />
        <Route path="contact" element={ <Contact/>} />
        <Route path="ourmission" element={ <OurMission/>} />
        <Route path="adopt" element={ <Adopt/>} />
        <Route path="singup" element={ <Signup/>} />
      </Route>

      </Routes>
      {bottomOfPage ? <Footer/>: null}


  </>
  );
}

export default App;
