import { useEffect } from "react";
import { useState } from "react";
import { createContext } from "react";
import localForage from "localforage";
import UserApi from "./functions/UserApi";

const AuthContext = createContext();

function AuthContextProvider({ children }) {
  const [authApiKey, setAuthApiKey] = useState('');
  const [authName, setAuthName] = useState('');
  const [authenticatedUser, setAuthenticatedUser] = useState(null);


  async function getFromForage() {
    const token = await localForage.getItem('token');
    //get token number from local storage data from key token
    console.log('token', token)
  }
  getFromForage() 
  useEffect(() => {
      getFromForage()
  }, [])

//   useEffect(() => {
//       localForage.setItem('petCreed', {
//           key: authApiKey,
//           email: authName
//       });
//   }, [authApiKey, authName])
  return (
    <AuthContext.Provider value={{ authApiKey, setAuthApiKey, authName, setAuthName }}>
    {children}
    </AuthContext.Provider>
  );
}

export { AuthContext, AuthContextProvider };
