import React from 'react'
import { useState } from 'react'
import { Grid } from "../assets/UiKit/grid/Gird";
import { Line,Rows } from "../assets/UiKit/Line/Line";
import { useNavigate } from "react-router-dom";
import '../assets/scss/Login.scss'

export default function Login() {
    const {email, setEmail} = useState("")
    const {password, setPassword} = useState("")
    const navigate = useNavigate();
    async function login(email, password ) {
        try {
            const response = await fetch('http://localhost:8000/login' , {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({
                email: email,
                password: password
            })
        })
        const data = await response.json()
        console.log(data)
        //save token to local storage
        localStorage.setItem('token', data.token)
        } catch (error) {
            console.log(error)
        }
    }
    function handlesubmit(e) {
        e.preventDefault();
        login(email, password)
        // create if login successfull navigate home page else show error message


        navigate('/home')

    }

    return (
    <div>
      <h2>LogIn</h2>
      <Grid>
        <Rows>
            <form>
                <label>
                    Email:
                    <input type="email" email="email" 
                        onChange={(e) => {
                            setEmail(e.target.value)
                        }
                    }

                    />
                </label>
                <label>
                    Password:
                    <input type="password" password="password" 
                        onChange={(e) => {
                            setPassword(e.target.value)
                        }
                    }
                    />
                </label>
                <input type="submit" value="Submit" 
                />
                <button onClick={handlesubmit}>Submit</button>
                <h5

                >message</h5>
            </form>


        </Rows>
      </Grid>

    </div>
  )
}
