import React from 'react'
import LowerNav from '../Components/HeadComponenets/LowerNav'
import TopMessage from '../Components/HeadComponenets/TopMessage'
import UpperNav from '../Components/HeadComponenets/UpperNav'
import '../assets/css/Head.css'
import LowerNavPhone from '../Components/HeadComponenets/LowerNavPhone'
import { useState, useEffect } from 'react';
export default function Head() {

  const ScreenSize = () => {
    const [display, setDisplay] = useState('x');

    useEffect(() => {
      const updateDisplay = () => {
        if (window.innerWidth > 769) {
          setDisplay(<LowerNav/>);
        } else {
          setDisplay(<LowerNavPhone/>);
        }
      };
      updateDisplay();
      window.addEventListener('resize', updateDisplay);
      return () => {
        window.removeEventListener('resize', updateDisplay);
      };
    }, []);

    return {display}
  };



  return (
    <div className='headContainer'>
    <div className='TopMessage'> 
    <TopMessage/>
    </div>

    <div className='UpperNav'>
        {ScreenSize}
    </div>
    <hr className='hr_standard'/>

    <div className='LowerNav'>
      <LowerNav/>
      </div>
    </div>

  )
}
