import React from 'react'
import { useState, useEffect } from 'react'
import LinkRow from '../Components/FooterComponenets/LinkRow'
import '../assets/scss/Footer.scss'
export default function Footer() {

  //show footer only when screen is scrolled all the way to the bottom
  const [showFooter, setShowFooter] = useState(false)
  const [scrollHeight, setScrollHeight] = useState(0)
  const [offsetHeight, setOffsetHeight] = useState(0)

  useEffect(() => {
    function handleScroll() {
      setScrollHeight(document.documentElement.scrollHeight)
      setOffsetHeight(document.documentElement.offsetHeight)
      if (scrollHeight - offsetHeight <= window.scrollY) {
        setShowFooter(true)
      } else {
        setShowFooter(false)
      }
    }
    window.addEventListener('scroll', handleScroll)
    return () => window.removeEventListener('scroll', handleScroll)
  }, [scrollHeight, offsetHeight])

  if (!showFooter) {
    return null
  }



  return (
    <div className='footerContainer'>
      <LinkRow/>
      <LinkRow/>
      <LinkRow/>
    </div>
  )
}
