import UserApi from "./Data/UserApi";

const AuthContext = createContext();

function AuthContextProvider({ children }) {
  const [token, setToken] = useState('');
  const [_id, setAuthId] = useState('');
  const [userData, setUserData] = useState(null);

async function getUserDataById(_id) {
    try {
    const userApi = new UserApi();   
    const userData = await userApi.getUserById(_id);   
    setUserData(userData) 
    console.log("userLogedIn "+userData);
    } catch (error) {
        console.log(error);
    }
}

async function getFromLocalForge(token) {
    try {

      const petApp = await localForage.getItem('petApp');
      if (petApp === null) {
        return;
      }
      if (petApp.token === token) {
        return;
      }
     setAuthId(petApp._id); 
     if (petApp._id == null) {
      return
     }
     getUserDataById(_id)
        return 
    } catch (error) {
      // console.log(error);
    }
  }

setInterval(() => {

    getFromLocalForge(token)

}, [20000])

useEffect(() => {

    console.log("id changed "+userData);
}, [userData])





  return (
    <AuthContext.Provider value={{userData  }}>
    {children}
    </AuthContext.Provider>
  );
}

export { AuthContext, AuthContextProvider };
