      async function handleSubmit(e) {
        e.preventDefault();
        try {
          const res = await userApi.login(registerEmail, registerPassWord);
          if (res.status === 200) {
            userApi.setToken(res.data.token, res.data._id);
            const userRes = await userApi.getUserById(res.data._id);
            if (userRes.status === 200) {
              setUserData(userRes.data);
              navigate("/home");
            } else {
              console.log(userRes);
              setErrorMessage("Unable to get user data");
            }
          } else {
            console.log(res);
            setErrorMessage("Please fill in all fields");
          }
        } catch (err) {
          console.log(err);
        }
      }


    return (
    <div>
      <h2>LogIn</h2>
      <Grid>
        <Rows>
            <form>
            <label>Email</label>
        <input
          type="email"
          value={registerEmail}
          onChange={(e) => setEmail(e.target.value)}
        />
        <label>Password</label>
        <input
          type="password"
          value={registerPassWord}
          onChange={(e) => setPassword(e.target.value)}
        />
                <button onClick={handleSubmit}>Submit</button>
                <h5

                >{errorMessage}</h5>
            </form>


        </Rows>
      </Grid>

    </div>
  )
}
